-- Drop the existing Persons1 table if it exists
IF OBJECT_ID('Persons1', 'U') IS NOT NULL
    DROP TABLE Persons1;

-- Create the Persons1 table
CREATE TABLE Persons1 (
    PersonID int,
    LastName varchar(255),
    FirstName varchar(255),
    City varchar(255)
);

-- Insert a record into the Persons1 table
INSERT INTO Persons1 (PersonID, LastName, FirstName, City)
